import React from "react";
import Header from "./Header";
import CreditCardForm from "./CreditCardForm";
import FormButton from "./FormButton";
import Image from "./Image";
import iconComplete from "../images/icon-complete.svg";

const App = () => {
  return (
    <div className="container">
      {/* pre filled-out state */}
      <Header
        cardNumber="1234 5678 9123 0000"
        cardHolder="Jane Appleseed"
        cardExpiration="00/00"
        cardCvc="000"
      />
      <CreditCardForm />

      {/* post filled-out state */}
      <Header cardNumber="" cardHolder="" cardExpiration="" cardCvc="" />
      <main className="completed-form-container">
        <Image
          source={iconComplete}
          altText="Check mark denoting form has been completed"
          namesOfClasses="check-mark-img"
        />
        <h2 className="completed-form-header">Thank You!</h2>
        <p className="completed-form-text">We've added your card details</p>
        <FormButton buttonText="Continue" />
      </main>
    </div>
  );
};

export default App;
