/////// JSX components
import Header from "./Header";
// import BackOfCard from "./BackOfCard";
// import FrontOfCard from "./FrontOfCard";
import FormField from "./FormField";
import FormButton from "./FormButton";
/////// Images
import iconComplete from "../images/icon-complete.svg";
// pass the state of the form (incomplete/complete) as a boolean prop into Header and make a Form component that receives the same prop
const App = () => {
  return (
    <div className="row">
      <Header />
      <div className="container">
        {/* pre filled-out state */}
        <main>
          <form className="form-container" action="">
            <FormField
              labelText="Cardholder Name"
              placeHolderText="e.g. Jane Appleseed"
            />
            <FormField
              labelText="Card Number"
              placeHolderText="e.g. 1234 5678 9123 0000"
            />
            <div className="form-bottom-row">
              <FormField labelText="Exp. Date" placeHolderText="MM" />
              <FormField labelText="(MM/YY)" placeHolderText="YY" />
              <FormField labelText="CVC" placeHolderText="e.g. 123" />
            </div>
            <FormButton
              specificStyles="form-button confirm-form-button"
              buttonText="Confirm"
            />
          </form>

          {/* post filled-out state */}

          {/* <BackOfCard cardCvc="123" />
          <FrontOfCard
            cardNumber="9591 6489 6389 1011"
            cardHolder="Felicia Leire"
            cardExpiration="09/26"
          />
          <section className="completed-form-container">
            <img
              src={iconComplete}
              alt="Check mark denoting form has been completed"
              className="check-mark-img"
            />
            <h2 className="completed-form-header">Thank You!</h2>
            <p className="completed-form-text">We've added your card details</p>
            <FormButton
              specificStyles="form-button continue-form-button"
              buttonText="Continue"
            />
          </section> */}
        </main>
      </div>
    </div>
  );
};

export default App;
